import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { DataService } from '../services/data.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  acno:any;
  uname:any;
  pswd:any;

  constructor(private ds:DataService, private router:Router, private fb:FormBuilder){}

  //model for register
  registerForm = this.fb.group({
    uname:['',[Validators.required,Validators.pattern('[a-zA-Z]*')]],
    acno:['',[Validators.required,Validators.pattern('[0-9]*')]],
    pswd:['',[Validators.required,Validators.pattern('[a-zA-Z0-9]*')]]
  })
  //control passed through html page
  
  register(){
    const acno = this.registerForm.value.acno;
    const uname = this.registerForm.value.uname;
    const pswd = this.registerForm.value.pswd;

    if(this.registerForm.valid){
      this.ds.register(acno, uname, pswd).subscribe(
        (result:any) => {
          alert(result.message);
          this.router.navigateByUrl('');
        },
        (result) => {
          alert(result.error.message);
        }
      )
    }

    // console.log(acno,pswd,uname);
    // const result = this.ds.register(acno, uname, pswd);
    // if(this.registerForm.valid){
    //   if (result){
    //     alert('Register successfully');
    //     this.router.navigateByUrl('');
    //   } else {
    //     alert('Registration Failed');
    //   }
    // }
  }


}
<!-- new register -->
  
<div class="container">
    <div class="register">
        <div class="reg-intro">
            <h2>Financial</h2>
            <h1>Fortress</h1>
            <p>Welcome to Financial Fortress, where your financial security is our top priority. Register today to take advantage of our secure and convenient banking services. With Financial Fortress, you can easily manage your money, deposit and withdraw funds, and stay on top of your account activity. Open an account today to discover a new level of financial security with us.</p>
        </div>
        <div class="reg-form">
            <h1>Create Account</h1>
            <form action="">
                <label for="">Account Number</label>
                <input type="text" placeholder="Enter Your Account Number"> <br>
                <label for="">Username</label>
                <input type="text" placeholder="Enter Username"> <br>
                <label for="">Password</label>
                <input type="text" placeholder="Enter Password"> <br>

                <button>Register</button>

                <p>Already have an account? <a href="">Sign In</a></p>
            </form>
       
        </div>
    </div>
</div>



<!-- old register -->
<div class="container">
    <div class="reg-body">
        <h2>Create account</h2>
        <form [formGroup]="registerForm">
            <input type="text"  formControlName="acno"  name="accountNumber" id="regAcno" placeholder="Account number">
            <div *ngIf="this.registerForm.get('acno')?.errors && this.registerForm.get('acno')?.touched" style="color: #ff4343;">invalid account number</div>

            <input type="text" formControlName="uname"  name="userName" id="regUname" placeholder="Username">
            <div *ngIf="this.registerForm.get('uname')?.errors && this.registerForm.get('uname')?.touched" style="color: #ff4343;">invalid user name</div>

            <input type="password"  formControlName="pswd" name="password" id="regPswd" placeholder="Password">
            <div *ngIf="this.registerForm.get('pswd')?.errors && this.registerForm.get('pswd')?.touched" style="color: #ff4343;">invalid password</div>

            <button type="button" (click)="register()">Register</button>
        </form>
       
        <p>already have an account?</p>
        <div style="width: fit-content; margin: auto;">
            <a routerLink="" class="hover-underline-animation">Sign in</a>
        </div>
    </div>
</div>
