import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { DataService } from '../services/data.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {

  acno: any;
  pswd: any;
  dAmt: any;
  acno1: any;
  pswd1: any;
  wAmt: any;
  user: any; // To hold current username
  sDate: any;
  constructor(private ds: DataService, private fb: FormBuilder, private router: Router) {
    if (localStorage.getItem('currentUser')) {
      this.sDate = Date();
      this.user = JSON.parse(localStorage.getItem('currentUser') || "");
    }
  }
  ngOnInit(): void {
    if (!localStorage.getItem('currentAcno')) {/////////
      alert('please login first');////////
      this.router.navigateByUrl('');///////
    }
  }

  depositForm = this.fb.group({
    acno: ['', [Validators.required, Validators.pattern('[0-9]*')]],
    pswd: ['', [Validators.required, Validators.pattern('[a-zA-Z0-9]*')]],
    dAmt: ['', [Validators.required, Validators.pattern('[0-9]*')]]
  })
  deposit() {
    const acno = this.depositForm.value.acno;
    const pswd = this.depositForm.value.pswd;
    const dAmt = this.depositForm.value.dAmt;
    const currentAcno = JSON.parse(localStorage.getItem('currentAcno') || "")
    if (this.depositForm.valid) {
      if (currentAcno == acno) {
        this.ds.deposit(acno, pswd, dAmt).subscribe(
          (result: any) => {
            alert(result.message);
          },
          (result: any) => {
            alert(result.error.message);
          })
      } else {
        alert('invalid user details')
      }
    }
  }

  withdrawForm = this.fb.group({
    acno1: ['', [Validators.required, Validators.pattern('[0-9]*')]],
    pswd1: ['', [Validators.required, Validators.pattern('[a-zA-Z0-9]*')]],
    wAmt: ['', [Validators.required, Validators.pattern('[0-9]*')]]
  })
  withdraw() {
    const acno1 = this.withdrawForm.value.acno1;
    const pswd1 = this.withdrawForm.value.pswd1;
    const wAmt = this.withdrawForm.value.wAmt;
    const currentAcno = JSON.parse(localStorage.getItem('currentAcno') || "")

    if (this.withdrawForm.valid) {
      if (acno1 == currentAcno) {
        const result = this.ds.withdraw(acno1, pswd1, wAmt).subscribe(
          (result: any) => {
            alert(result.message);
          },
          (result: any) => {
            alert(result.error.message);
          }
        )
      } else {
        alert('invalid user details');
      }
    }
  }

  logout() {
    localStorage.removeItem('currentAcno');
    localStorage.removeItem('currentUser');
    this.router.navigateByUrl('');
  }

  delete() {
    this.acno = JSON.parse(localStorage.getItem('currentAcno') || '');
  }

  onCancel() {
    this.acno = "";
  }

  onDelete(acno: any) {
    this.ds.deleteAcc(acno).subscribe(
      (result: any) => {
        alert(result.message);

        localStorage.removeItem('currentAcno');
        localStorage.removeItem('currentUser');

        this.router.navigateByUrl('');
      },
      (result) => {
        alert(result.error.message);
      }
    )
  }
}
<div class="container">
    <div class="header">
        <div class="header-user"><h1>Welcome {{user}}</h1></div>
        <p style="color: white;width: 30%;" class="date" >{{sDate | date}}</p>
        <div ><a routerLink="/trasaction" class="transaction">view trasactions</a></div>
        <div class="header-btn">
            <input type="button" value="Delete" (click)="delete()" class="delete-btn" >
            <input type="button" value="Logout" (click)="logout()" class="logout-btn" >
        </div>
       
    </div>
    <app-delete [item]="acno" (Oncancel)="onCancel()" (OnDelete)="onDelete($event)" ></app-delete>
    <div [ngClass]="acno? 'style1': '' " class="container-2" >
        <div class="deposit">
            <div class="dp-wt-body">
                <h2>Deposit</h2>
                    <form [formGroup]="depositForm">
                        <input type="text" formControlName="acno" name="accountNumber" id="dpAcno" placeholder="Account number">
                        <div *ngIf="this.depositForm.get('acno')?.errors && this.depositForm.get('acno')?.touched" style="color: #ff4343;">invalid account number</div>
    
                        <input type="text" formControlName="pswd" name="password" id="dpPswd" placeholder="Password">
                        <div *ngIf="this.depositForm.get('pswd')?.errors && this.depositForm.get('pswd')?.touched" style="color: #ff4343;">invalid password </div>
    
                        <input type="text" formControlName="dAmt"  name="depositAmount" id="dpAmount" placeholder="Amount">
                        <div *ngIf="this.depositForm.get('dAmt')?.errors && this.depositForm.get('dAmt')?.touched" style="color: #ff4343;">invalid amount</div>
    
                        <button type="button" (click)="deposit()">Deposit</button>
                    </form>
            </div>
        </div>
        <div class="withdraw">
            <div class="dp-wt-body">
                <h2>Withdraw</h2>
                <form [formGroup]="withdrawForm">
                    <input type="text" formControlName="acno1" name="accountNumber" id="wtAcno" placeholder="Account number">
                    <div *ngIf="this.withdrawForm.get('acno1')?.errors && this.withdrawForm.get('acno1')?.touched" style="color: #ff4343;">invalid account number</div>

                    <input type="text" formControlName="pswd1" name="password" id="wtPswd" placeholder="Password">
                    <div *ngIf="this.withdrawForm.get('pswd1')?.errors && this.withdrawForm.get('pswd1')?.touched" style="color: #ff4343;">invalid password</div>

                    <input type="text" formControlName="wAmt"  name="withdrawAmount" id="wtAmount" placeholder="Amount">
                    <div *ngIf="this.withdrawForm.get('wAmt')?.errors && this.withdrawForm.get('wAmt')?.touched" style="color: #ff4343;">invalid Amount</div>

                    <button type="button" (click)="withdraw()">withdraw</button>
                </form>

            </div>
        </div>
    </div>
</div>
