import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-delete',
  templateUrl: './delete.component.html',
  styleUrls: ['./delete.component.css']
})
export class DeleteComponent {

  constructor(){}
  // To hold value from parent
  @Input() item : string | undefined;
  @Output() Oncancel = new EventEmitter();
  
  cancel(){
    this.Oncancel.emit();
  }
}
<div *ngIf="item" style=" width: fit-content; margin: auto; padding: 10px;">
    <h4> Are you sure do you want to delete this account {{item}}?</h4>
    <button type="button" (click)="delete()"
        style="background-color: rgb(220, 53, 69); width: 40px;padding: 5px; border-radius: 5px;
             color: white; margin: 0 10px; margin-left: 130px;">yes</button>
    <button type="button" (click)="cancel()"
        style="background-color: rgb(25, 135, 84); width: 40px;padding: 5px; border-radius: 5px; 
            color: white; margin: 0 10px;">No</button>
</div>
