import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class DataService {

  currentUser:any;// To hold currentuser
  currentAcno:any; // To hold current acno

  userDetails:any = {
    1000 : {acno:1000, username:'Amal', password:1000, balance:2000, transaction:[]},
    1001 : {acno:1001, username:'Arun', password:1001, balance:2000, transaction:[]},
    1002 : {acno:1002, username:'Akshay', password:1002, balance:2000, transaction:[]}
  }

  constructor(private http:HttpClient) { }

  saveDetails(){
    if(this.userDetails){
      localStorage.setItem('Database',JSON.stringify(this.userDetails));

      localStorage.setItem('currentUser',JSON.stringify(this.currentUser));

      localStorage.setItem('currentAcno',JSON.stringify(this.currentAcno));
    }
  }

  getDetails(){
    if(localStorage.getItem('Database')){
      this.userDetails = JSON.parse(localStorage.getItem('Database') || '');
    }
    if(localStorage.getItem('currentAcno')){
      this.currentAcno = JSON.parse(localStorage.getItem('currenAcno') || '');
    }
    if(localStorage.getItem('currentUser')){
      this.currentUser = JSON.parse(localStorage.getItem('currentUser') || '');
    }
  }

  register(acno:any, uname:any, pswd:any){
    
    const body = {
      acno,
      username,
      password
    }
  }

  login(acno:any, pswd:any){
    let userDetails = this.userDetails;
    
    if(acno in userDetails){
      if(pswd == userDetails[acno].password){
        this.currentUser = userDetails[acno].username;
        this.currentAcno = acno;
        this.saveDetails();
        return true;
      }else{
        alert('Invalid password');
        return false;
      }
    }else{
      alert('Invalid user');
      return false;
    }
  }

  deposit(acno:any, pswd:any, dAmt:any){
    let userDetails = this.userDetails;

    if (acno in userDetails){
      if (pswd == userDetails[acno].password){
        dAmt = parseInt(dAmt);
        userDetails[acno].balance += dAmt;

        userDetails[acno]['transaction'].push({
          type:'Credit',
          amount:dAmt
        })
        this.saveDetails();
        return userDetails[acno].balance;
      }else {
        alert('Invalid password');
        return false;
      }
    }else{
      alert('Invalid user');
      return false
    }
  }
  
  withdraw(acno1:any, pswd1:any, wAmt:any){
    let userDetails = this.userDetails;

    if(acno1 in userDetails){
      if(pswd1 == userDetails[acno1].password){
        wAmt = parseInt(wAmt);
        if(userDetails[acno1].balance > wAmt){
          userDetails[acno1].balance -= wAmt;

          userDetails[acno1]['transaction'].push({
            type:'Debit',
            amount:wAmt
          })
          this.saveDetails();
          return userDetails[acno1].balance;
        } else{
          alert('insufficient balance');
          return false;
        }
      }else{
        alert('Invalid password');
        return false;
      }
    }else{
      alert('Invalid user');
      return false;
    }
  }

  getTransaction(acno:any){
    this.saveDetails();
    return this.userDetails[acno]['transaction'];
  }

  
}
